# Generated by Selenium IDE
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.chrome.service import Service
from webdriver_manager.chrome import ChromeDriverManager
from selenium.webdriver.support import expected_conditions as EC
from selenium.webdriver.support.ui import WebDriverWait


class TestRate():
    def setup_method(self, method):
        options = webdriver.ChromeOptions()
        self.driver = webdriver.Chrome(service=Service(ChromeDriverManager().install()), options=options)
        self.vars = {}
  
    def teardown_method(self, method):
        self.driver.quit()
  
    def test_rate(self):
        self.driver.get("http://localhost:5000/login")
        self.driver.set_window_size(1050, 748)
        self.driver.find_element(By.ID, "email").click()
        self.driver.find_element(By.ID, "email").send_keys("user1@example.com")
        self.driver.find_element(By.ID, "password").click()
        self.driver.find_element(By.ID, "password").send_keys("1234")
        self.driver.find_element(By.ID, "submit").click()
        self.driver.find_element(By.LINK_TEXT, "Sample dataset 4").click()
        self.driver.find_element(By.CSS_SELECTOR, ".star:nth-child(4)").click()
        self.driver.find_element(By.ID, "rate-btn").click()
        
        # Esperar hasta que la alerta esté presente antes de intentar acceder a ella
        WebDriverWait(self.driver, 10).until(EC.alert_is_present())
        
        # Ahora puedes acceder a la alerta con seguridad
        alert = self.driver.switch_to.alert
        assert alert.text == "Calificación guardada"
        alert.accept()  # Esto cerrará la alerta después de verificar su contenido
        
        self.driver.find_element(By.CSS_SELECTOR, ".text-dark").click()
        self.driver.find_element(By.CSS_SELECTOR, ".dropdown-menu > .dropdown-item:nth-child(2)").click()
        self.driver.close()
